select universidad.persona.apellido1, universidad.persona.apellido2, universidad.persona.nombre from universidad.persona order by universidad.persona.apellido1 asc, universidad.persona.apellido2 asc, universidad.persona.nombre asc; 
select universidad.persona.nombre, universidad.persona.apellido1, universidad.persona.apellido2  from universidad.persona where universidad.persona.telefono is null;
select * from universidad.persona where year(fecha_nacimiento) = 1999;
select * from universidad.persona where (universidad.persona.tipo = 'profesor' and universidad.persona.telefono is null and nif like '%K');
select * from universidad.asignatura where universidad.asignatura.curso = 3 and universidad.asignatura.cuatrimestre = 1 and universidad.asignatura.id_grado = 7;
select universidad.persona.apellido1, universidad.persona.apellido2, universidad.persona.nombre, universidad.departamento.nombre from universidad.persona inner join universidad.profesor on (universidad.persona.id = universidad.profesor.id_profesor) inner join universidad.departamento on (universidad.profesor.id_departamento = universidad.departamento.id) order by universidad.persona.apellido1 asc, universidad.persona.apellido2 asc, universidad.persona.nombre asc;
select universidad.asignatura.nombre, universidad.curso_escolar.anyo_inicio, universidad.curso_escolar.anyo_finfrom universidad.asignatura inner join universidad.alumno_se_matricula_asignatura on (universidad.asignatura.id = universidad.alumno_se_matricula_asignatura.id_asignatura) inner join universidad.persona on (universidad.alumno_se_matricula_asignatura.id_alumno = universidad.persona.id) inner join universidad.curso_escolar on (universidad.asignatura.curso = universidad.curso_escolar.id) where universidad.persona.nif = '26902806M'; 
select distinctrow universidad.departamento.nombre from universidad.departamento inner join universidad.profesor on (universidad.departamento.id = universidad.profesor.id_departamento) inner join universidad.asignatura on (universidad.profesor.id_profesor = universidad.asignatura.id_profesor) inner join universidad.grado on (universidad.asignatura.id_grado = universidad.grado.id) where universidad.grado.nombre = 'Grado en Ingeniería Informática (Plan 2015)';
select distinctrow universidad.persona.nombre, universidad.persona.apellido1, universidad.persona.apellido2, universidad.curso_escolar.anyo_inicio from universidad.persona inner join universidad.alumno_se_matricula_asignatura on (universidad.persona.id = universidad.alumno_se_matricula_asignatura.id_alumno) inner join universidad.curso_escolar on (universidad.alumno_se_matricula_asignatura.id_curso_escolar = universidad.curso_escolar.id) where universidad.curso_escolar.anyo_inicio = 2018;
select universidad.departamento.nombre as nombre_departamento, universidad.persona.apellido1, universidad.persona.apellido2, universidad.persona.nombre from universidad.departamento right join universidad.profesor on (universidad.profesor.id_departamento = universidad.departamento.id) right join universidad.persona on (universidad.profesor.id_profesor = universidad.persona.id) order by universidad.departamento.nombre asc, universidad.persona.apellido1 asc, universidad.persona.apellido2 asc, universidad.persona.nombre asc;
select universidad.departamento.nombre as nombre_departamento, universidad.persona.apellido1, universidad.persona.apellido2, universidad.persona.nombre from universidad.departamento right join universidad.profesor on (universidad.profesor.id_departamento = universidad.departamento.id) right join universidad.persona on (universidad.profesor.id_profesor = universidad.persona.id) where universidad.departamento.nombre is null; 
select universidad.departamento.nombre as nombre_departamento, universidad.persona.apellido1, universidad.persona.apellido2, universidad.persona.nombre from universidad.departamento left join universidad.profesor on (universidad.profesor.id_departamento = universidad.departamento.id) left join universidad.persona on (universidad.profesor.id_profesor = universidad.persona.id) where universidad.persona.nombre is null and universidad.persona.apellido1 is null and universidad.persona.apellido2 is null;
select distinctrow universidad.persona.apellido1, universidad.persona.apellido2, universidad.persona.nombre from universidad.persona left join universidad.asignatura on (universidad.persona.id = universidad.asignatura.id_profesor) where universidad.persona.tipo = 'profesor' and universidad.asignatura.nombre is null;
select distinct universidad.asignatura.nombre from universidad.persona right join universidad.asignatura on (universidad.persona.id = universidad.asignatura.id_profesor) where universidad.persona.nombre is null and universidad.persona.apellido1 is null and universidad.persona.apellido2 is null;
select distinct universidad.departamento.nombre from universidad.departamento left join universidad.profesor on (universidad.departamento.id = universidad.profesor.id_departamento) where universidad.profesor.id_profesor is null; 
select universidad.persona.tipo, count(*) as numero_alumnos from universidad.persona where universidad.persona.tipo = 'alumno';
select year(universidad.persona.fecha_nacimiento) as anio_nacimiento, count(*) as numero_Alumnos from universidad.persona where year(universidad.persona.fecha_nacimiento) = 1999;  
select universidad.departamento.nombre as nombre_departamento, count(universidad.profesor.id_departamento) as numero_profesores from universidad.profesor inner join universidad.departamento on (universidad.profesor.id_departamento = universidad.departamento.id) group by universidad.departamento.nombre order by numero_profesores desc;
select universidad.departamento.nombre as nombre_departamento, count(universidad.profesor.id_departamento) as numero_profesores from universidad.profesor right join universidad.departamento on (universidad.profesor.id_departamento = universidad.departamento.id) group by universidad.departamento.nombre;
select universidad.grado.nombre as nombre_grados_existentes, count(universidad.asignatura.id_grado) as numero_asignaturas from universidad.grado left join universidad.asignatura on (universidad.grado.id = universidad.asignatura.id_grado) group by universidad.grado.nombre order by numero_asignaturas desc; 
select universidad.grado.nombre as nombre_grados_existentes, count(universidad.asignatura.id_grado) as numero_asignaturas from universidad.grado left join universidad.asignatura on (universidad.grado.id = universidad.asignatura.id_grado) group by universidad.grado.nombre having numero_asignaturas > 40; 
select universidad.grado.nombre as nombre_grado, universidad.asignatura.tipo as tipo_asignatura, sum(universidad.asignatura.creditos) as total_creditos_existentes from universidad.grado left join universidad.asignatura on (universidad.grado.id = universidad.asignatura.id_grado) group by universidad.grado.nombre, universidad.asignatura.tipo; 
select distinctrow universidad.curso_escolar.anyo_inicio, count(distinctrow universidad.alumno_se_matricula_asignatura.id_alumno) as numero_alumnos_matriculados from universidad.curso_escolar inner join universidad.alumno_se_matricula_asignatura on (universidad.curso_escolar.id = universidad.alumno_se_matricula_asignatura.id_curso_escolar) group by universidad.curso_escolar.anyo_inicio;
select universidad.persona.id, universidad.persona.nombre, universidad.persona.apellido1, universidad.persona.apellido2, count(universidad.asignatura.nombre) as asignatura from universidad.persona left join universidad.asignatura on (universidad.asignatura.id_profesor = universidad.persona.id) where universidad.persona.tipo = 'profesor' group by universidad.persona.id order by asignatura desc;
select * from universidad.persona order by universidad.persona.fecha_nacimiento desc limit 1; 
select distinctrow universidad.persona.id, universidad.persona.nombre, universidad.persona.apellido1, universidad.persona.apellido2 from universidad.persona left join universidad.profesor on (universidad.persona.id = universidad.profesor.id_profesor) left join universidad.asignatura on (universidad.profesor.id_profesor = universidad.asignatura.id_profesor) where universidad.persona.tipo = 'profesor' and universidad.asignatura.nombre is null;